// Extract account ID (UUID) from CloudMailin forwarding address
const email = $input.item.json;

let accountId = null;

// Method 1: Extract UUID from envelope.to (CloudMailin format)
// Format: prefix+UUID@cloudmailin.net
if (email.body?.envelope?.to) {
  const match = email.body.envelope.to.match(/\+([a-f0-9-]{36})@/i);
  if (match) accountId = match[1];
}

// Method 2: Try recipients array
if (!accountId && email.body?.envelope?.recipients?.[0]) {
  const match = email.body.envelope.recipients[0].match(/\+([a-f0-9-]{36})@/i);
  if (match) accountId = match[1];
}

// Method 3: Try headers.to
if (!accountId && email.body?.headers?.to) {
  const match = email.body.headers.to.match(/\+([a-f0-9-]{36})@/i);
  if (match) accountId = match[1];
}

// Validation
if (!accountId) {
  throw new Error('Unable to extract account ID from email');
}

return {
  json: {
    ...email,
    accountId,
    extractedAt: new Date().toISOString()
  }
};

const email = $('Extract Account ID').item.json;
const detection = $('Detect Vendor (API)').item.json;
const cleaned = $('Clean Email').item.json;

// Generate vendorKey from vendorName (lowercase, no spaces)
const vendorName = detection.vendorName || detection.vendor || 'Unknown';
const vendorKey = vendorName.toLowerCase().replace(/[^a-z0-9]/g, '');

// Merge all data
return {
  json: {
    // Email data
    accountId: email.accountId,
    subject: email.body?.headers?.subject || '',
    from: email.body?.headers?.from || '',
    receivedAt: email.body?.headers?.date || new Date().toISOString(),

    // Email content (prefer cleaned HTML)
    html: cleaned?.cleanedHtml || email.body?.html,
    originalHtml: email.body?.html,
    plain: email.body?.plain,
    attachments: email.body?.attachments || [],

    // Vendor detection - use vendorName for display, vendorKey for routing
    vendor: vendorName,
    vendorKey: vendorKey,
    confidence: detection.confidence || 0,

    // Cleaning metadata
    emailCleaned: !!cleaned?.cleanedHtml,
    detectedProviders: cleaned?.detectedProviders || [],

    // Processing metadata
    workflowStartedAt: new Date().toISOString()
  }
};

// Inline vendor configuration (will be moved to Supabase later)
// parseSource: 'html' = parse email HTML body, 'pdf' = parse PDF attachment
const vendorConfigs = {
  'modernoptical': { vendorKey: 'modernoptical', requiresEnrichment: true, parseSource: 'html' },
  'safilo': { vendorKey: 'safilo', requiresEnrichment: false, parseSource: 'pdf' },
  'luxottica': { vendorKey: 'luxottica', requiresEnrichment: false, parseSource: 'html' },
  'europa': { vendorKey: 'europa', requiresEnrichment: false, parseSource: 'html' },
  'etniabarcelona': { vendorKey: 'etnia-barcelona', requiresEnrichment: false, parseSource: 'pdf' },
  'idealoptics': { vendorKey: 'idealoptics', requiresEnrichment: true, parseSource: 'html' },
  'lamyamerica': { vendorKey: 'lamy', requiresEnrichment: false, parseSource: 'html' },
  'kenmark': { vendorKey: 'kenmark', requiresEnrichment: false, parseSource: 'html' },
  'marchon': { vendorKey: 'marchon', requiresEnrichment: true, parseSource: 'html' }
};

const data = $input.item.json;

// Use vendorKey for lookup (already lowercase, no spaces from Format API Response)
const lookupKey = data.vendorKey || data.vendor?.toLowerCase().replace(/[^a-z0-9]/g, '') || '';
const config = vendorConfigs[lookupKey];

if (!config) {
  return {
    json: {
      ...data,
      vendorConfig: null,
      routeTo: 'unknown_vendor',
      _debug: { lookupKey, availableKeys: Object.keys(vendorConfigs) }
    }
  };
}

// Route based on enrichment needs (parseSource is handled in Parse Vendor Email node)
return {
  json: {
    ...data,
    vendorConfig: config,
    routeTo: config.requiresEnrichment ? 'enrichment_handler' : 'standard_processor'
  }
};

const data = $input.item.json;

// Check if config loaded
if (!data.vendorConfig) {
  return {
    json: {
      ...data,
      error: 'Vendor configuration not found',
      routeTo: 'unknown_vendor'
    }
  };
}

// Validation passed
return {
  json: {
    ...data
  }
};

