// Get parsed data and catalog check results
const parsedData = $('Parse Vendor Email').item.json;
const catalogCheck = $('Check Catalog').item.json;
const originalData = $('Validate Vendor Config').item.json;

// Merge catalog check info into items
const itemsWithCatalogInfo = parsedData.items.map(item => {
  // Find matching cached item from catalog check
  const catalogInfo = catalogCheck.items?.find(c => 
    c.sku === item.sku || 
    c.sku === item.model ||
    c.model === item.model
  );

  // If cached, use cached data for UPC and pricing
  const isCached = catalogInfo?.cached === true;

  // Item needs enrichment if:
  // 1. Not cached AND vendor requires enrichment, OR
  // 2. Item is missing UPC (and vendor requires enrichment)
  const needsEnrichment = !isCached && 
                          (catalogInfo?.needsEnrichment || 
                          (!item.upc && originalData.vendorConfig?.requiresEnrichment));

  return {
    ...item,
    // Use cached UPC if available, otherwise use parsed UPC
    upc: catalogInfo?.upc || item.upc,
    // Use cached wholesale price if available
    wholesale_price: catalogInfo?.wholesale_price || catalogInfo?.wholesale_cost || item.wholesale_price,
    // Use cached MSRP if available
    msrp: catalogInfo?.msrp || item.msrp,
    sku: item.sku || item.model,
    model: item.model || item.sku,
    description: item.description || `${item.brand} ${item.model} ${item.color}`.trim(),
    existsInCatalog: isCached,
    catalogPrice: catalogInfo?.wholesale_price || catalogInfo?.wholesale_cost || catalogInfo?.catalogPrice,
    needsEnrichment: needsEnrichment,
    cached: isCached
  };
});

// CRITICAL: Use DYNAMIC vendor data from parser response
const vendor = parsedData.vendor || originalData.vendor;
const vendorKey = parsedData.vendorKey || originalData.vendorKey;
const vendorId = parsedData.vendorId;
const accountId = parsedData.accountId || originalData.accountId;

// Get email metadata from original data
const fromEmail = originalData.from || '';
const subject = originalData.subject || '';

// Determine if we need enrichment:
// - Vendor must require enrichment AND
// - At least one item needs enrichment (missing UPC or flagged by catalog)
const shouldEnrich = originalData.vendorConfig?.requiresEnrichment && 
                     itemsWithCatalogInfo.some(item => item.needsEnrichment);

console.log(`Vendor: ${vendor}, requiresEnrichment: ${originalData.vendorConfig?.requiresEnrichment}`);
console.log(`Items needing enrichment: ${itemsWithCatalogInfo.filter(i => i.needsEnrichment).length}/${itemsWithCatalogInfo.length}`);
console.log(`Should enrich: ${shouldEnrich}`);

return {
  json: {
    vendor: vendor,
    vendorKey: vendorKey,
    vendorId: vendorId,
    accountId: accountId,
    from: fromEmail,
    subject: subject,
    order: parsedData.order,
    items: itemsWithCatalogInfo,
    catalogCheckResult: catalogCheck,

    // For Create Email Record API - include parsed data!
    emailData: {
      vendor: vendor,
      vendorId: vendorId,
      accountId: accountId,
      from: fromEmail,
      subject: subject,
      html: originalData.html,
      plainText: originalData.plain,
      parsedData: {
        order: parsedData.order,
        items: itemsWithCatalogInfo,
        vendor: vendor,
        vendorId: vendorId
      },
      parseStatus: 'success'
    },

    // For Bulk-Add Items API - needs vendor (name), order object, items
    bulkAddData: {
      accountId: accountId,
      vendor: vendor,
      order: {
        order_number: parsedData.order?.order_number,
        order_date: parsedData.order?.order_date,
        customer_name: parsedData.order?.customer_name,
        rep_name: parsedData.order?.rep_name
      },
      items: itemsWithCatalogInfo.map(item => ({
        sku: item.sku || item.model,
        model: item.model,
        brand: item.brand,
        color: item.color,
        color_code: item.color_code,
        size: item.size || item.eye_size,
        description: item.description,
        quantity: item.quantity,
        wholesale_price: item.wholesale_price,
        upc: item.upc
      }))
    },

    // For Cache to Catalog API - needs model, brand per item
    cacheData: {
      vendorId: vendorId,
      items: itemsWithCatalogInfo.map(item => ({
        model: item.model,
        brand: item.brand,
        color: item.color,
        color_code: item.color_code,
        color_name: item.color_name,
        size: item.size || item.eye_size,
        sku: item.sku || item.model,
        description: item.description,
        wholesale_price: item.wholesale_price,
        upc: item.upc,
        image_url: item.image_url,
        product_url: item.product_url
      }))
    },

    // Check if enrichment needed
    needsEnrichment: shouldEnrich
  }
};

