// Merge enriched data back into items
const preparedData = $('Prepare Email Data').item.json;
const enrichedResult = $('Enrich').item.json;

// Create lookup map of enriched items by model (Marchon uses model for matching)
const enrichedMap = new Map();
if (enrichedResult?.enrichedItems) {
  // Marchon enrichment returns enrichedItems array
  enrichedResult.enrichedItems.forEach(item => {
    const key = item.model || item.sku;
    if (key) enrichedMap.set(key.toUpperCase(), item);
  });
} else if (enrichedResult?.items) {
  // Fallback for other vendors
  enrichedResult.items.forEach(item => {
    const key = item.model || item.sku;
    if (key) enrichedMap.set(key.toUpperCase(), item);
  });
}

console.log('Enrichment map size:', enrichedMap.size);

// Merge enriched data into items
const mergedItems = preparedData.items.map(item => {
  const lookupKey = (item.model || item.sku || '').toUpperCase();
  const enrichedInfo = enrichedMap.get(lookupKey);

  if (enrichedInfo) {
    console.log(`Enriched ${item.model}: UPC=${enrichedInfo.upc}, Wholesale=${enrichedInfo.wholesale_price}`);
  }

  return {
    ...item,
    // Marchon enrichment fields
    upc: enrichedInfo?.upc || item.upc,
    wholesale_price: enrichedInfo?.wholesale_price || item.wholesale_price,
    msrp: enrichedInfo?.msrp || item.msrp,
    in_stock: enrichedInfo?.in_stock ?? item.in_stock,
    api_verified: enrichedInfo?.api_verified || false,
    confidence_score: enrichedInfo?.confidence_score,
    // Enrichment metadata
    enriched: !!enrichedInfo,
    enriched_data: enrichedInfo?.enriched_data || item.enriched_data
  };
});

// Update all data structures with enriched items
return {
  json: {
    ...preparedData,
    items: mergedItems,
    emailData: {
      ...preparedData.emailData,
      parsedData: {
        ...preparedData.emailData.parsedData,
        items: mergedItems
      }
    },
    bulkAddData: {
      ...preparedData.bulkAddData,
      items: mergedItems.map(item => ({
        sku: item.sku || item.model,
        model: item.model,
        brand: item.brand,
        color: item.color,
        color_code: item.color_code,
        size: item.size || item.eye_size,
        description: item.description,
        quantity: item.quantity,
        upc: item.upc,
        wholesale_price: item.wholesale_price,
        msrp: item.msrp,
        in_stock: item.in_stock,
        api_verified: item.api_verified
      }))
    },
    cacheData: {
      ...preparedData.cacheData,
      items: mergedItems.map(item => ({
        model: item.model,
        brand: item.brand,
        color: item.color,
        color_code: item.color_code,
        color_name: item.color_name,
        size: item.size || item.eye_size,
        sku: item.sku || item.model,
        description: item.description,
        price: item.wholesale_price || item.price,
        upc: item.upc,
        image_url: item.image_url,
        product_url: item.product_url,
        msrp: item.msrp,
        wholesale_price: item.wholesale_price
      }))
    },
    enrichmentApplied: true,
    enrichmentStats: {
      totalItems: mergedItems.length,
      enrichedCount: mergedItems.filter(i => i.enriched).length,
      apiVerifiedCount: mergedItems.filter(i => i.api_verified).length
    }
  }
};

