// Get results from all three sequential nodes
let emailResult = {};
let bulkAddResult = {};
let cacheResult = {};

try {
  emailResult = $('Create Email Record').item.json || {};
} catch (e) {
  emailResult = { error: 'Node not executed' };
}

try {
  bulkAddResult = $('Bulk-Add Items').item.json || {};
} catch (e) {
  bulkAddResult = { error: 'Node not executed' };
}

try {
  cacheResult = $('Cache to Catalog').item.json || {};
} catch (e) {
  cacheResult = { error: 'Node not executed' };
}

// Try to get data from enrichment path first, fall back to prepared data
let preparedData;
try {
  const enrichedData = $('Merge Enrichment Results').item.json;
  if (enrichedData) {
    preparedData = enrichedData;
  } else {
    preparedData = $('Prepare Email Data').item.json;
  }
} catch (e) {
  // Enrichment path wasn't taken, use prepared data
  preparedData = $('Prepare Email Data').item.json;
}

// Check for any failures (look for error property or success:false)
const failures = [];
if (emailResult.error || emailResult.success === false) {
  failures.push(`Create Email Record: ${emailResult.error || 'Failed'}`);
}
if (bulkAddResult.error || bulkAddResult.success === false) {
  failures.push(`Bulk-Add Items: ${bulkAddResult.error || 'Failed'}`);
}
if (cacheResult.error || cacheResult.success === false) {
  failures.push(`Cache to Catalog: ${cacheResult.error || 'Failed'}`);
}

return {
  json: {
    success: failures.length === 0,
    message: failures.length === 0 ? 'Order processed successfully' : `Partial failure: ${failures.join(', ')}`,

    // Results from each step
    emailRecordId: emailResult.emailId || emailResult.email?.id,
    itemsAdded: bulkAddResult.itemsAdded || bulkAddResult.addedCount || 0,
    itemsCached: cacheResult.itemsCached || cacheResult.cachedCount || 0,

    // Summary
    vendor: preparedData.vendor,
    orderId: preparedData.order?.order_number,
    itemsProcessed: preparedData.items?.length || 0,

    // For metrics
    workflowStartedAt: $('Format API Response').item.json.workflowStartedAt,
    workflowEndedAt: new Date().toISOString(),

    // Failures (if any)
    failures: failures.length > 0 ? failures : null,

    // Debug info
    _debug: {
      emailResult: emailResult,
      bulkAddResult: bulkAddResult,
      cacheResult: cacheResult,
      usedEnrichmentPath: preparedData.enrichmentApplied || false
    }
  }
};
